function constructVoxels(json_object){
	var cubes = json_object.cubes;

	//()for
	var keys = Object.keys(cubes);

	for(var i=0; i<keys.length; i++){
		var color = keys[i];

		//for
		var xyzArray = cubes[color];

		for(var j=0; j<xyzArray.length; j++){
			var xyz = xyzArray[j];

			var x = xyz.x;
			var y = xyz.y;
			var z = xyz.z;

			//
			generateVoxel(x,y,z,color);
		}
	}

}

